import React, { Component } from 'react';
import axios from 'axios';

class ProductDetail extends Component{
	state = {
		'data':'',
	}


	render(){

		const FetchDetail=(id)=>{
			const url = 'http://127.0.0.1:8000/product/'+id;
			axios.get(url).then(res=>{
			this.setState(res.data);
			}).catch(err=>{ 
				console.error(err);
			});
		}
		FetchDetail(this.props.ProductId);
		return(
			<div className = 'container'>

				<div className='jumbotron navBar'
				style={{padding:'5px 0px'}}
				><h3 className='text-center'>NPR</h3>
				</div>

				<hr className='TopHr'/>

				<h6 className='btn btn-danger btn-sm' 
				onClick={this.props.handleBack}>Back</h6>


				<div className="container">
					<img src={this.state.Image} 
					className='productDetailImg'
					alt='Product img'/>
					<h1 className='productDetailTittle'>{this.state.Name}</h1>
					<div className='container'>
						<h6>Likes : {this.state.no_of_likes} </h6>
						<h6>Dislikes : {this.state.no_of_dislikes}</h6>
						<h6>Comments : {this.state.no_of_comments}</h6>
						<h6>Launch Date : {this.state.LaunchDate}</h6>
					</div>
					<div className='container comments'>
						<h3>COMMENTS</h3>
						<div className='container'>

						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default ProductDetail;
