import React, { Component } from 'react';
import axios from 'axios';

class Login extends Component{
	state = {
		'fieldIsPassword':true,

		'username':'',
		'password':'',
	}
	showPassword=()=>{
		if(this.state.btnType==='text') this.setState({'btnType':'password',
		'showBtnText':'show'});
		else this.setState({'btnType':'text','showBtnText':'Hide'});
		}

	handleLogin=()=>{
		const data = {'username':this.state.username, 'password':this.state.password}
		axios.post('http://127.0.0.1:8000/login/', data)
		.then((res)=>{
			console.log(res.data);
		}).catch((err)=>{
			console.log(err);
		})
	}
	render(){
		return(
			<div>
				<form className='form-group'>

					<label>Username :</label>
					<input type='text' 
					className='form-control' 
					placeholder='e.g. sumit@123'
					onChange={e=>{this.setState({'username':e.target.value})}}
					/><br/>

					<label>Password :</label>
					<input type={this.state.btnType} 
					className='form-control' 
					placeholder='e.g. password@123'
					onChange={e=>{this.setState({'password':e.target.value})}}
					/><br/>
					<h1 
					onClick = {()=>{
					this.setState({'fieldIsPassword':!(this.state.fieldIsPassword)})}
					}
					className='btn btn-danger btn-sm'
					>{this.state.fieldIsPassword?'password':'text'}</h1><br/><br/>

					<h1 type='submit' 
					className='btn btn-success m-2 form-control' 
					onClick={this.handleLogin}
					>LOGIN</h1>
				</form>
			</div>
		);
	}
}

export default Login;
