import React, { Component } from 'react';
import axios from 'axios';

class Signup extends Component{
	state = {
		'btnType':'password',
		'btnType2':'password',
		'showBtnText':'Show',
		'showBtnText2':'Show',


		'username':'',
		'emial':'',
		'first_name':'',
		'last_name':'',
		'password':'',
		'password2':'',

	}
	showPassword=()=>{
		if(this.state.btnType==='text') this.setState({'btnType':'password','showBtnText':'Show'});
		else this.setState({'btnType':'text','showBtnText':'Hide'});
	}
	showPassword2=()=>{
		if(this.state.btnType2==='text') this.setState({'btnType2':'password','showBtnText2':'Show'});
		else this.setState({'btnType2':'text','showBtnText2':'Hide'});
	}

	handleSignup=()=>{
		const data = {
			'username':this.state.username,
			'emial':this.state.emial,
			'first_name':this.state.first_name,
			'last_name':this.state.last_name,
			'password':this.state.password,
			'password2':this.state.password2,
		}
		axios.post('http://127.0.0.1:8000/signup/', data).then(res=>{
			this.setState(res.data);
		}).catch(err=>{
			console.error(err);
		});
	}
	render(){
		return(
			<div>
				<form className='form-group'>

					<label>Username :</label>
					<input type='text' 
					className='form-control' 
					placeholder='e.g. sumit123'
					onChange={e=>{this.setState({'username':e.target.value})}}
					/><br/>

					<label>First Name :</label>
					<input type='text' 
					className='form-control' 
					placeholder='e.g. Sumit'
					onChange={e=>{this.setState({'first_name':e.target.value})}}
					/><br/>

					<label>Last Name :</label>
					<input type='text' 
					className='form-control' 
					placeholder='e.g. Dhakad'
					onChange={e=>{this.setState({'last_name':e.target.value})}}
					/><br/>

					<label>Email :</label>
					<input type='email' 
					className='form-control' 
					placeholder='e.g. sumitdhakad123@gmail.com'
					onChange={e=>{this.setState({'emial':e.target.value})}}
					/><br/>

					<label>Password :</label>
					<input type={this.state.btnType} 
					className='form-control' 
					placeholder='Make strong password.'
					onChange={e=>{this.setState({'password':e.target.value})}}
					/><br/>
					<h1 
					onClick = {this.showPassword}
					className='btn btn-danger btn-sm'
					>{this.state.showBtnText}</h1><br/><br/>

					<label>Confirm password :</label>
					<input type={this.state.btnType2} 
					className='form-control' 
					placeholder='Both password should be same.'
					onChange={e=>{this.setState({'password2':e.target.value})}}
					/><br/>
					<h1 
					onClick = {this.showPassword2}
					className='btn btn-danger btn-sm'
					>{this.state.showBtnText2}</h1><br/><br/>

					<h1 type='submit' 
					className='btn btn-success m-2 form-control'
					onClick = {this.handleSignup}
					>SIGNUP</h1>
				</form>
			</div>
		);
	}
}

export default Signup;
