import React, { Component } from 'react';
import Product from './Product';
import axios from 'axios';

class AllProducts extends Component{
	state = {
		'data':[],
		'filteredData':[],
		'searchData':''
	}

	componentDidMount(){
		axios.get('http://127.0.0.1:8000/productList/?format=json').then(res=>{
			this.setState({'data':res.data,'filteredData':res.data});
		}).catch(err=>{ 
			console.error(err);
		});
	}

	render(){
		return(
			<div className='cotainer'>
				<div className='jumbotron form-inline'
					style={{padding:'5px 4px'}}
					>
					<h3 className='m-2'>NPR</h3>

					<div className='cotainer'>
						<input type='search'

						value={this.state.searchData}

						onChange={e=>{
							this.setState({'filteredData':this.state.data,
							'searchData':e.target.value.toUpperCase()});
							this.setState({'filteredData':this.state.filteredData
							.filter(d=>d.product_name.includes(this.state.searchData))}); 
						}}

						className='form-control p-1 cm-10' 
						placeholder='Search product'
						/>

						<input type='submit' 
						className='btn btn-primary btn-sm cm-10'				
						value='Search'
						/>
					</div>
				</div>
				<hr/>
				<div>
					{this.state.filteredData.map((d)=>{return(
						<div className='container' key={d.id}>	
							<Product
							ProductName={d.product_name}
							Image={d.Image}
							Likes={d.no_of_likes}
							Comments={d.no_of_comments}
							Dislikes={d.no_of_dislikes}
							LaunchDate={d.launched_on}
							onClick={()=>this.props.onClick(d.id)}
							/>
						</div>
					)})}
				</div>
			</div>
		);
	}
}

export default AllProducts;
