import React, { Component } from 'react';

class Signup extends Component{
	state = {
		'btnType':'password',
		'btnType2':'password',
		'showBtnText':'Show',
		'showBtnText2':'Show'
	}
	showPassword=()=>{
		if(this.state.btnType=='text') this.setState({'btnType':'password','showBtnText':'Show'});
		else this.setState({'btnType':'text','showBtnText':'Hide'});
	}
	showPassword2=()=>{
		if(this.state.btnType2=='text') this.setState({'btnType2':'password','showBtnText2':'Show'});
		else this.setState({'btnType2':'text','showBtnText2':'Hide'});
	}
	render(){
		return(
			<div>
				<form className='form-group'>

					<label>Username :</label>
					<input type='text' 
					className='form-control' 
					placeholder='e.g. sumit123'
					/><br/>

					<label>First Name :</label>
					<input type='text' 
					className='form-control' 
					placeholder='e.g. Sumit'
					/><br/>

					<label>Last Name :</label>
					<input type='text' 
					className='form-control' 
					placeholder='e.g. Dhakad'
					/><br/>

					<label>Email :</label>
					<input type='email' 
					className='form-control' 
					placeholder='e.g. sumitdhakad123@gmail.com'
					/><br/>

					<label>Password :</label>
					<input type={this.state.btnType} 
					className='form-control' 
					placeholder='Make strong password.'
					/><br/>
					<h1 
					onClick = {this.showPassword}
					className='btn btn-danger btn-sm'
					>{this.state.showBtnText}</h1><br/><br/>

					<label>Confirm password :</label>
					<input type={this.state.btnType2} 
					className='form-control' 
					placeholder='Both password should be same.'
					/><br/>
					<h1 
					onClick = {this.showPassword2}
					className='btn btn-danger btn-sm'
					>{this.state.showBtnText2}</h1><br/><br/>

					<input type='submit' 
					className='btn btn-success m-2 form-control' 
					value='SIGNUP'
					/>
				</form>
			</div>
		);
	}
}

export default Signup;
