import React, { Component } from 'react';
import AllProducts from './AllProducts';
import ProductDetail from './ProductDetail';

class Screen2 extends Component{
	state = {
		'detailedProductPage':true,
		'idToFetchDetail':0,
	}
	handleProductDetial=(id)=>{
		this.setState({'detailedProductPage':false});
	}

	handlingBackEvent=()=>{
		this.setState({'detailedProductPage':true});
	}
	render(){
		return(
			<div className='cotainer'>
				<div className='jumbotron form-inline'
				style={{padding:'5px 4px'}}
				>
				<h3 className='m-2'>NPR</h3>

					<div className='cotainer'>
						<input type='search' 
						className='form-control p-1 cm-10' 
						placeholder='Search product'
						/>

						<input type='submit' 
						className='btn btn-primary btn-sm cm-10'				
						value='Search'
						/>
					</div>
				</div>
				{this.state.detailedProductPage?<ProductDetail
				handleBack={this.handlingBackEvent}
				ProductId={this.state.idToFetchDetail}
				/>:<AllProducts 
				onClick={this.handleProductDetial}/>}
			</div>
		);
	}
}

export default Screen2;
