import React, { Component } from 'react';

class Login extends Component{
	state = {
		'btnType':'password',
		'showBtnText':'Show',

		'username':'',
		'password':'',
	}
	showPassword=()=>{
		if(this.state.btnType==='text') this.setState({'btnType':'password',
		'showBtnText':'show'});
		else this.setState({'btnType':'text','showBtnText':'Hide'});
		}

	handleLogin=()=>{
		axios.post('http://127.0.0.1:8000/login/', [this.state.username,
													this.state.password]);
		console.log(this.state.username);
		console.log(this.state.password);
	}
	render(){
		return(
			<div>
				<form className='form-group'>

					<label>Username :</label>
					<input type='text' 
					className='form-control' 
					placeholder='e.g. sumit@123'
					onChange={e=>{this.setState({'username':e.target.value})}}
					/><br/>

					<label>Password :</label>
					<input type={this.state.btnType} 
					className='form-control' 
					placeholder='e.g. password@123'
					onChange={e=>{this.setState({'password':e.target.value})}}
					/><br/>
					<h1 
					onClick = {this.showPassword}
					className='btn btn-danger btn-sm'
					>{this.state.showBtnText}</h1><br/><br/>

					<h1 type='submit' 
					className='btn btn-success m-2 form-control' 
					onClick={this.handleLogin}
					>LOGIN</h1>
				</form>
			</div>
		);
	}
}

export default Login;
