import React,{ Component } from 'react';
import Counter from './Counter';

class Counters extends Component {
	state = {
		counters : [
			{id : 1, value : 1},
			{id : 2, value : 0},
			{id : 3, value : 0},
			{id : 4, value : 0},

		],
	};

	handleOnDelete(counterId){
		const counters = this.state.counters.filter(c=>c.id!==counterId);
		this.setState({counters});
	};

	handleReset=()=>{
		const counters = this.state.counters.map(c=>{c.value=0;return c;});
		this.setState({counters});
	};

	handleIncrement(counterId){
		const counters = this.state.counters.map(
		c=>{c.value=(c.id===counterId)?(c.value+1):c.value;
		return c;});
		this.setState({counters});				
	};

	render(){
		return(
			<div>
				<button
				className="btn btn-primary btn-sm m-2"
				onClick={this.handleReset}
				>Reset
				</button>
				{this.state.counters.map(counter=><Counter
				 key={counter.id}
				 value={counter.value}
				 onDelete = {()=>this.handleOnDelete(counter.id)}
				 onIncrement = {()=>this.handleIncrement(counter.id)}
				 />)}

			</div>
		);
	};
};

export default Counters;
