import React, { Component } from 'react';
import Login from './Login';
import Signup from './Signup';

class Screen1 extends Component{
	state = {
		'loginPage':true,
		'btnText':'Signup',
	};
	pageChange=()=>{
		if(this.state.loginPage) this.setState({'loginPage':false, 'btnText':'Login'});
		else this.setState({'loginPage':true, 'btnText':'Signup'});
	}
	render(){
		return(
			<div className='container'>
				<div className='jumbotron'
				style={{padding:'5px 0px'}}
				><h1 className='text-center'>NPR</h1></div>
				<button className='btn btn-default m-2 btn-sm' 
				onClick={this.pageChange}
				>{this.state.btnText}</button>
				<hr/>
				{this.state.loginPage?<Login/>:<Signup/>}

			</div>
		);
	};
};

export default Screen1;
